mapper.select:
SELECT id, user_id, tokens_remaining, search_remaining, created_at, updated_at
FROM api_usage
WHERE user_id = #{userId}

mapper.update:
UPDATE api_usage
SET tokens_remaining = #{tokensRemaining}, updated_at = NOW()
WHERE id = #{id}

UPDATE api_usage
SET search_remaining = #{searchRemaining}, updated_at = NOW()
WHERE id = #{id}
